D3js.script_render:
const margin = {top: 40, right: 40, bottom: 60, left: 60};
const colors = ['#1f77b4', '#ff7f0e', '#2ca02c', '#d62728', '#9467bd', '#8c564b', '#e377c2', '#7f7f7f', '#bcbd22', '#17becf'];
function render() {
    d3.select('#chart').selectAll('*').remove();
    const width = window.innerWidth - margin.left - margin.right;
    const height = window.innerHeight - margin.top - margin.bottom;
    const svg = d3.select('#chart').append('svg')
        .attr('width', width + margin.left + margin.right)
        .attr('height', height + margin.top + margin.bottom)
        .append('g').attr('transform', `translate(${margin.left},${margin.top})`);
    const x = d3.scaleBand().range([0, width]).domain(dataset.map(d => d.value.x)).padding(0.1);
    const y = d3.scaleLinear().range([height, 0]).domain([0, 80]);
    svg.append('g').attr('transform', `translate(0,${height})`).call(d3.axisBottom(x));
    svg.append('g').call(d3.axisLeft(y));
    svg.selectAll('.bar').data(dataset).enter().append('rect')
        .attr('class', 'bar')
        .attr('x', d => x(d.value.x) + (x.bandwidth() - parseFloat(d.render['bar-width']))/2)
        .attr('width', d => parseFloat(d.render['bar-width']))
        .attr('y', d => y(parseFloat(d.value.y)))
        .attr('height', d => height - y(parseFloat(d.value.y)))
        .attr('fill', (d, i) => colors[i]);
    svg.selectAll('.text').data(dataset).enter().append('text')
        .attr('x', d => x(d.value.x) + x.bandwidth()/2).attr('y', d => y(parseFloat(d.value.y)) + 15)
        .attr('text-anchor', 'middle').text(d => d.value.y).style('font-size', '16px');
}
render();
window.addEventListener('resize', render);

echarts.script_render:
const chart = echarts.init(document.getElementById('chart'));
const option = {
    xAxis: { type: 'category', data: dataset.map(d => d.value.x) },
    yAxis: { type: 'value', max: 80 },
    series: [{
        data: dataset.map((d, i) => ({
            value: parseFloat(d.value.y),
            itemStyle: { color: ['#1f77b4', '#ff7f0e', '#2ca02c', '#d62728', '#9467bd', '#8c564b', '#e377c2', '#7f7f7f', '#bcbd22', '#17becf'][i] }
        })),
        type: 'bar',
        barWidth: parseFloat(dataset[0].render['bar-width']),
        label: { show: true, position: 'insideTop', fontSize: 16, color: '#000' }
    }]
};
chart.setOption(option);
window.addEventListener('resize', () => chart.resize());

vega.script_render:
const spec = {
    $schema: 'https://vega.github.io/schema/vega-lite/v5.json',
    width: 'container', height: 'container',
    data: { values: dataset.map(d => ({ x: d.value.x, y: parseFloat(d.value.y) })) },
    layer: [
        { mark: { type: 'bar', size: parseFloat(dataset[0].render['bar-width']) },
          encoding: {
            x: { field: 'x', type: 'nominal', axis: { labelAngle: 0 } },
            y: { field: 'y', type: 'quantitative', scale: { domain: [0, 80] } },
            color: { field: 'x', type: 'nominal', legend: null }
          }
        },
        { mark: { type: 'text', dy: 15, fontSize: 16 },
          encoding: {
            x: { field: 'x', type: 'nominal' },
            y: { field: 'y', type: 'quantitative' },
            text: { field: 'y', type: 'quantitative' }
          }
        }
    ]
};
vegaEmbed('#chart', spec, { actions: false });
